const mongoose = require("mongoose")
// Define Mongoose Schema
const CustomizationSchema = new mongoose.Schema({
  componentName: { 
    type: String, 
    required: true 
  },
  variantId: { 
    type: String, 
    required: true 
  },
  userInput: { 
    type: String, 
    required: true 
  },
  generatedCode: { 
    type: String, 
    required: true 
  },
  timestamp: { 
    type: Date, 
    default: Date.now 
  },
  // Auto-expire after 30 minutes
  createdAt: { 
    type: Date, 
    default: Date.now,
    expires: 60 * 30 // 30 minutes in seconds
  }
});

// Create and export the model
const CustomizationModel = mongoose.model('Customization', CustomizationSchema, 'customizations');

module.exports = CustomizationModel;
